package com.magadistudio.javabasicoperation;

public class JavaBasicOpertaion 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
	
	    //explanation of the function of "println"
		//println里面只有字符串，则其他项也全部按照字符串处理
		System.out.println('a' + 1);
		System.out.println(" " + 'a' + 1);
		System.out.println("123" + 234 + 1);
		System.out.println("bhf" + 234 + 1);
		System.out.println('b' + 'a' + 1);
		
	    //addtional operator
	}

}
